import { useState, useRef } from "react";
import { galleryImages } from "@/data/galleryData";
import { CarouselTopBar } from "./carousel/CarouselTopBar";
import { CarouselNavigation } from "./carousel/CarouselNavigation";
import { CarouselImage } from "./carousel/CarouselImage";
import { ImageInfo } from "./carousel/ImageInfo";
import { ImageIndicators } from "./carousel/ImageIndicators";
import { useCarouselKeyboard } from "./carousel/useCarouselKeyboard";
import { useImageColor } from "./carousel/useImageColor";

interface ImageCarouselProps {
  currentIndex: number;
  onIndexChange: (index: number) => void;
  onViewModeChange: () => void;
}

export function ImageCarousel({
  currentIndex,
  onIndexChange,
  onViewModeChange,
}: ImageCarouselProps) {
  const [isLoading, setIsLoading] = useState(true);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const imageRef = useRef<HTMLImageElement | null>(null);

  const currentImage = galleryImages[currentIndex];
  const { dominantColor, updateDominantColor } = useImageColor(
    imageRef,
    currentImage.id,
    currentIndex,
  );

  const handleImageLoad = async () => {
    setIsLoading(false);
    setIsTransitioning(false);
    await updateDominantColor();
  };

  const handlePrevious = () => {
    const newIndex =
      currentIndex === 0 ? galleryImages.length - 1 : currentIndex - 1;
    onIndexChange(newIndex);
    setIsLoading(true);
    setIsTransitioning(true);
  };
  const [currentIndex, setCurrentIndex] = useState(initialIndex);
  const handleNext = () => {
    const newIndex =
      currentIndex === galleryImages.length - 1 ? 0 : currentIndex + 1;
    onIndexChange(newIndex);
    setIsLoading(true);
    setIsTransitioning(true);
  };

  const carouselRef = useRef<HTMLDivElement>(null);
    onPrevious: handlePrevious,
    onNext: handleNext,
    onViewModeChange,
    currentIndex,
    onNext: () => goToNext(),
  useEffect(() => {
    if (carouselRef.current && initialIndex > 0) {
    <div
      className="min-h-screen w-full transition-colors duration-500 ease-out relative overflow-hidden"
      style={{
        backgroundColor: dominantColor,
        contain: "layout style paint",
      }}
    >
      {/* Background gradient overlay */}
      <div className="absolute inset-0 bg-gradient-to-b from-black/15 via-transparent to-black/30" />

    }
  }, [initialIndex]);

        onViewModeChange={onViewModeChange}


      {/* Main image container */}
      <div className="flex items-center justify-center min-h-screen px-6 md:px-20">
        <div className="relative max-w-5xl w-full">
          <CarouselNavigation
            onPrevious={handlePrevious}
            onNext={handleNext}
            isTransitioning={isTransitioning}
          />
  return (
          <CarouselImage
            key={currentImage.id}
            ref={imageRef}
            src={currentImage.src}
            alt={currentImage.title}
            blurDataURL={currentImage.blurDataURL}
            onLoad={handleImageLoad}
            priority={true}
          />

          <ImageInfo
            title={currentImage.title}
            description={currentImage.description}
          />
        </div>
      </div>
                onLoad={() => handleImageLoad(image.id)}
      <ImageIndicators
        totalImages={galleryImages.length}
        currentIndex={currentIndex}
        onIndexChange={onIndexChange}
      />

        <ImageInfo
          image={currentImage}
          currentIndex={currentIndex}
          totalImages={galleryImages.length}
        />
      </div>
    </div>
  );
}

"use client";

import { useState, useRef, useEffect } from "react";
import { CarouselImage } from "./carousel/CarouselImage";
import { CarouselNavigation } from "./carousel/CarouselNavigation";
import { CarouselTopBar } from "./carousel/CarouselTopBar";
import { ImageIndicators } from "./carousel/ImageIndicators";
import { ImageInfo } from "./carousel/ImageInfo";
import { galleryImages, type GalleryImage } from "@/data/galleryData";
import { useCarouselKeyboard } from "./carousel/useCarouselKeyboard";

interface ImageCarouselProps {
  initialIndex?: number;
  onClose?: () => void;
}

export function ImageCarousel({
  initialIndex = 0,
  onClose,
}: ImageCarouselProps) {
  const [currentIndex, setCurrentIndex] = useState(initialIndex);
  const [imagesLoaded, setImagesLoaded] = useState<Record<string, boolean>>({});
  const carouselRef = useRef<HTMLDivElement>(null);

  // Handle keyboard navigation
  useCarouselKeyboard({
    onNext: () => goToNext(),
    onPrevious: () => goToPrevious(),
    onClose: onClose,
  });

  const goToNext = () => {
    if (currentIndex < galleryImages.length - 1) {
      const newIndex = currentIndex + 1;
      setCurrentIndex(newIndex);
      scrollToImage(newIndex);
    }
  };

  const goToPrevious = () => {
    if (currentIndex > 0) {
      const newIndex = currentIndex - 1;
      setCurrentIndex(newIndex);
      scrollToImage(newIndex);
    }
  };

  const goToImage = (index: number) => {
    setCurrentIndex(index);
    scrollToImage(index);
  };

  const scrollToImage = (index: number) => {
    if (carouselRef.current) {
      const imageElement = carouselRef.current.children[index] as HTMLElement;
      if (imageElement) {
        imageElement.scrollIntoView({
          behavior: "smooth",
          block: "nearest",
          inline: "center",
        });
      }
    }
  };

  const handleImageLoad = (imageId: string) => {
    setImagesLoaded((prev) => ({ ...prev, [imageId]: true }));
  };

  // Handle scroll snap detection to update current index
  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) return;

    const handleScroll = () => {
      const scrollLeft = carousel.scrollLeft;
      const imageWidth = carousel.clientWidth;
      const newIndex = Math.round(scrollLeft / imageWidth);

      if (
        newIndex !== currentIndex &&
        newIndex >= 0 &&
        newIndex < galleryImages.length
      ) {
        setCurrentIndex(newIndex);
      }
    };

    carousel.addEventListener("scroll", handleScroll, { passive: true });
    return () => carousel.removeEventListener("scroll", handleScroll);
  }, [currentIndex]);

  // Initialize scroll position
  useEffect(() => {
    if (carouselRef.current && initialIndex > 0) {
      scrollToImage(initialIndex);
    }
  }, [initialIndex]);

  const currentImage = galleryImages[currentIndex];

  return (
    <div className="fixed inset-0 bg-black/90 backdrop-blur-sm z-50 flex flex-col">
      <CarouselTopBar
        currentIndex={currentIndex}
        totalImages={galleryImages.length}
        onClose={onClose}
      />

      <div className="flex-1 relative overflow-hidden">
        {/* Main carousel container with scroll snap */}
        <div
          ref={carouselRef}
          className="flex h-full overflow-x-auto snap-x snap-mandatory scrollbar-hide"
          style={{
            scrollbarWidth: "none",
            msOverflowStyle: "none",
          }}
        >
          {galleryImages.map((image, index) => (
            <div
              key={image.id}
              className="flex-shrink-0 w-full h-full flex items-center justify-center snap-center px-4"
            >
              <CarouselImage
                src={image.src}
                alt={image.title}
                onLoad={() => handleImageLoad(image.id)}
                priority={Math.abs(index - currentIndex) <= 1}
                blurDataURL={image.blurDataURL}
              />
            </div>
          ))}
        </div>

        {/* Navigation arrows */}
        <CarouselNavigation
          onNext={goToNext}
          onPrevious={goToPrevious}
          canGoNext={currentIndex < galleryImages.length - 1}
          canGoPrevious={currentIndex > 0}
        />
      </div>

      {/* Bottom controls */}
      <div className="flex-shrink-0 p-6 space-y-4">
        <ImageIndicators
          images={galleryImages}
          currentIndex={currentIndex}
          onImageSelect={goToImage}
        />

        <ImageInfo
          image={currentImage}
          currentIndex={currentIndex}
          totalImages={galleryImages.length}
        />
      </div>
    </div>
  );
}

import { forwardRef, useState } from "react";
import Image from "next/image";
import { Loader2 } from "lucide-react";

interface CarouselImageProps {
  src: string;
  alt: string;
  onLoad: () => void;
  priority?: boolean;
  blurDataURL?: string;
}

export const CarouselImage = forwardRef<HTMLImageElement, CarouselImageProps>(
  ({ src, alt, onLoad, priority = false, blurDataURL }, ref) => {
    const [imageLoaded, setImageLoaded] = useState(false);

    const handleImageLoad = () => {
      setImageLoaded(true);
      onLoad();
    };

    return (
      <div className="relative rounded-3xl overflow-hidden shadow-intense bg-black/15 transform-gpu">
        {/* Blur placeholder - always visible initially, fades out when image loads */}
        {blurDataURL && (
          <>
            <div
              className={`absolute inset-0 flex items-center justify-center w-full h-full bg-cover bg-center transform-gpu transition-opacity duration-500 ease-out ${
                imageLoaded ? "motion-opacity-out" : "motion-preset-fade"
              }`}
              style={{
                backgroundImage: `url(${blurDataURL})`,
                filter: "blur(20px)",
                transform: "scale(1.1)", // Slightly scale to hide blur edges
                willChange: "opacity",
              }}
            ></div>
            {!imageLoaded && (
              <div className="text-gallery-text/80 absolute inset-0 flex items-center justify-center z-20 h-full w-full">
                <Loader2 className="size-8 m-auto animate-spin" />
              </div>
            )}
          </>
        )}

        <Image
          ref={ref}
          width={800}
          height={600}
          src={src}
          alt={alt}
          priority={priority}
          quality={90}
          sizes="(max-width: 768px) 100vw, (max-width: 1200px) 80vw, 70vw"
          className={`w-full h-auto max-h-[70vh] aspect-[16/10] object-cover transform-gpu transition-opacity duration-500 ease-out ${
            imageLoaded ? "opacity-100" : "opacity-0"
          }`}
          onLoad={handleImageLoad}
          style={{ willChange: "opacity" }}
        />
      </div>
    );
  },
);

CarouselImage.displayName = "CarouselImage";

import Image from "next/image";
import { useState } from "react";

interface CarouselImageProps {
  src: string;
  alt: string;
  onLoad?: () => void;
  priority?: boolean;
  blurDataURL?: string;
}

export function CarouselImage({
  src,
  alt,
  onLoad,
  priority = false,
  blurDataURL,
}: CarouselImageProps) {
  const [isLoaded, setIsLoaded] = useState(false);

  const handleLoad = () => {
    setIsLoaded(true);
    onLoad?.();
  };

  return (
    <div className="relative w-full h-full flex items-center justify-center p-4">
      <div className="relative max-w-full max-h-full">
        <Image
          src={src}
          alt={alt}
          width={1200}
          height={800}
          className={`max-w-full max-h-full object-contain rounded-2xl shadow-2xl transition-all duration-500 ${
            isLoaded ? "opacity-100 scale-100" : "opacity-0 scale-95"
          }`}
          onLoad={handleLoad}
          priority={priority}
          placeholder={blurDataURL ? "blur" : "empty"}
          blurDataURL={blurDataURL}
          sizes="(max-width: 768px) 100vw, (max-width: 1200px) 90vw, 80vw"
        />

        {/* Loading state with blur image */}
        {!isLoaded && blurDataURL && (
          <div className="absolute inset-0 rounded-2xl overflow-hidden">
            <Image
              src={blurDataURL}
              alt=""
              fill
              className="object-contain scale-110 blur-sm"
              priority
            />
            <div className="absolute inset-0 bg-black/20 rounded-2xl flex items-center justify-center">
              <div className="w-8 h-8 border-2 border-white/40 border-t-white rounded-full animate-spin" />
            </div>
          </div>
        )}

        {/* Fallback loading for images without blur */}
        {!isLoaded && !blurDataURL && (
          <div className="absolute inset-0 rounded-2xl bg-gray-900/30 backdrop-blur-sm flex items-center justify-center">
            <div className="w-8 h-8 border-2 border-white/40 border-t-white rounded-full animate-spin" />
          </div>
        )}
      </div>
    </div>
  );
}

import { ChevronLeft, ChevronRight } from "lucide-react";

interface CarouselNavigationProps {
  onNext: () => void;
  onPrevious: () => void;
  canGoNext: boolean;
  canGoPrevious: boolean;
}

export function CarouselNavigation({
  onNext,
  onPrevious,
  canGoNext,
  canGoPrevious,
}: CarouselNavigationProps) {
  return (
    <>
      {/* Previous button */}
      <button
        onClick={onPrevious}
        disabled={!canGoPrevious}
        className={`absolute left-6 top-1/2 -translate-y-1/2 z-10 p-4 rounded-full bg-black/30 backdrop-blur-xl border border-white/15 transition-all duration-300 ease-out shadow-lg ${
          canGoPrevious
            ? "hover:bg-black/50 hover:scale-105 hover:shadow-xl active:scale-95 opacity-100"
            : "opacity-40 cursor-not-allowed"
        }`}
        aria-label="Previous image"
      >
        <ChevronLeft className="w-6 h-6 text-white" strokeWidth={2.5} />
      </button>

      {/* Next button */}
      <button
        onClick={onNext}
        disabled={!canGoNext}
        className={`absolute right-6 top-1/2 -translate-y-1/2 z-10 p-4 rounded-full bg-black/30 backdrop-blur-xl border border-white/15 transition-all duration-300 ease-out shadow-lg ${
          canGoNext
            ? "hover:bg-black/50 hover:scale-105 hover:shadow-xl active:scale-95 opacity-100"
            : "opacity-40 cursor-not-allowed"
        }`}
        aria-label="Next image"
      >
        <ChevronRight className="w-6 h-6 text-white" strokeWidth={2.5} />
      </button>
    </>
  );
}

