interface ImageIndicatorsProps {
  images: GalleryImage[];
  currentIndex: number;
  onImageSelect: (index: number) => void;
}

export function ImageIndicators({
  images,
  currentIndex,
  onImageSelect,
}: ImageIndicatorsProps) {
  return (
    <div className="flex justify-center px-4">
      <div className="flex items-center gap-3 p-3 h-[90px] bg-black/20 backdrop-blur-xl rounded-2xl border border-white/10 max-w-full overflow-x-auto scrollbar-hide shadow-lg">
        {images.map((image, index) => (
          <button
            key={image.id}
            onClick={() => onImageSelect(index)}
            className={`relative flex-shrink-0 rounded-xl overflow-hidden transition-all duration-300 ease-out ${
              index === currentIndex
                ? "w-16 h-16 ring-2 ring-white shadow-lg scale-110"
                : "w-12 h-12 ring-1 ring-white/20 hover:ring-white/40 hover:scale-105 active:scale-95"
            }`}
            aria-label={`Go to image ${index + 1}: ${image.title}`}
          >
            <Image
              src={image.src}
              alt={image.title}
              fill
              sizes="64px"
              className="object-cover"
              placeholder={image.blurDataURL ? "blur" : "empty"}
              blurDataURL={image.blurDataURL}
            />
            {/* Current image overlay */}
            {index === currentIndex && (
              <div className="absolute inset-0 bg-gradient-to-t from-black/30 via-transparent to-transparent" />
            )}
            {/* Active indicator dot */}
            {index === currentIndex && (
              <div className="absolute bottom-1 left-1/2 transform -translate-x-1/2 w-1.5 h-1.5 bg-white rounded-full shadow-sm" />
            )}
          </button>
        ))}
      </div>
    </div>
  );
}

import Image from "next/image";
import { useEffect, useRef } from "react";
import { type GalleryImage } from "@/data/galleryData";

interface ImageIndicatorsProps {
  images: GalleryImage[];
  currentIndex: number;
  onImageSelect: (index: number) => void;
}

export function ImageIndicators({
  images,
  currentIndex,
  onImageSelect,
}: ImageIndicatorsProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const activeButtonRef = useRef<HTMLButtonElement>(null);

  // Scroll to active thumbnail when currentIndex changes
  useEffect(() => {
    if (containerRef.current && activeButtonRef.current) {
      const container = containerRef.current;
      const activeButton = activeButtonRef.current;

      const containerRect = container.getBoundingClientRect();
      const buttonRect = activeButton.getBoundingClientRect();

      // Calculate if button is outside visible area
      const isButtonVisible =
        buttonRect.left >= containerRect.left &&
        buttonRect.right <= containerRect.right;

      if (!isButtonVisible) {
        // Calculate scroll position to center the active button
        const buttonCenter = activeButton.offsetLeft + activeButton.offsetWidth / 2;
        const containerCenter = container.offsetWidth / 2;
        const scrollPosition = buttonCenter - containerCenter;

        container.scrollTo({
          left: scrollPosition,
          behavior: "smooth",
        });
      }
    }
  }, [currentIndex]);

  return (
    <div className="flex justify-center px-4">
      <div
        ref={containerRef}
        className="flex items-center gap-3 p-3 bg-black/20 backdrop-blur-xl rounded-2xl border border-white/10 max-w-full overflow-x-auto scrollbar-hide shadow-lg"
      >
        {images.map((image, index) => (
          <button
            key={image.id}
            ref={index === currentIndex ? activeButtonRef : null}
            onClick={() => onImageSelect(index)}
            className={`relative flex-shrink-0 rounded-xl overflow-hidden transition-all duration-300 ease-out ${
              index === currentIndex
                ? "w-16 h-16 ring-2 ring-white shadow-lg scale-110"
                : "w-12 h-12 ring-1 ring-white/20 hover:ring-white/40 hover:scale-105 active:scale-95"
            }`}
            aria-label={`Go to image ${index + 1}: ${image.title}`}
          >
            <Image
              src={image.src}
              alt={image.title}
              fill
              sizes="64px"
              className="object-cover"
              placeholder={image.blurDataURL ? "blur" : "empty"}
              blurDataURL={image.blurDataURL}
            />
            {/* Current image overlay */}
            {index === currentIndex && (
              <div className="absolute inset-0 bg-gradient-to-t from-black/30 via-transparent to-transparent" />
            )}
            {/* Active indicator dot */}
            {index === currentIndex && (
              <div className="absolute bottom-1 left-1/2 transform -translate-x-1/2 w-1.5 h-1.5 bg-white rounded-full shadow-sm" />
            )}
          </button>
        ))}
      </div>
    </div>
  );
}

import { Share2 } from "lucide-react";
import { GlassButton } from "@/components/ui/glass-button";
import { toast } from "sonner";

interface ImageInfoProps {
  title: string;
  description: string;
}

export function ImageInfo({ title, description }: ImageInfoProps) {
  const handleShare = async () => {
    try {
      if (navigator.share) {
        await navigator.share({
          title,
          text: description,
          url: window.location.href,
        });
      } else {
        await navigator.clipboard.writeText(window.location.href);
        toast.success("Link copied to clipboard!");
      }
    } catch (error) {
      console.error("Error sharing:", error);
      toast.error("Failed to share image");
    }
  };

  image,
    <div className="mt-8 text-center space-y-4">
      <div className="space-y-2">
        <h2 className="text-xl  font-bold text-gallery-text">{title}</h2>
        <p className="text-gallery-muted text-base max-w-2xl mx-auto">
          {description}
        </p>
      </div>

      <div className="flex items-center justify-center gap-4 pt-4">
        <GlassButton
          onClick={handleShare}
          className="hover:scale-105 transition-transform duration-200"
        >
          <Share2 size={18} className="mr-2" />
          Share
        </GlassButton>
      </div>
      <h3 className="font-medium text-lg mb-1">{image.alt}</h3>
      <p className="text-sm text-white/60">
        Image {currentIndex + 1} of {totalImages}
      </p>
    </div>
  );
}

import { type GalleryImage } from "@/data/galleryData";

interface ImageInfoProps {
  image: GalleryImage;
  currentIndex: number;
  totalImages: number;
}

export function ImageInfo({
  image,
  currentIndex,
  totalImages,
}: ImageInfoProps) {
  return (
    <div className="text-center text-white px-6 max-w-2xl mx-auto">
      <h3 className="font-semibold text-xl mb-2 text-white/95 tracking-tight leading-tight">
        {image.title}
      </h3>
      <p className="text-sm text-white/70 leading-relaxed mb-3 font-medium">
        {image.description}
      </p>
      <p className="text-xs text-white/50 font-medium tracking-wide">
        {currentIndex + 1} of {totalImages}
      </p>
    </div>
  );
}

import { useEffect } from "react";

interface UseCarouselKeyboardProps {
  onNext: () => void;
  onPrevious: () => void;
  onClose?: () => void;
}

export function useCarouselKeyboard({
  onNext,
  onPrevious,
  onClose,
}: UseCarouselKeyboardProps) {
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      switch (event.key) {
        case "ArrowRight":
        case " ": // Spacebar
          event.preventDefault();
          onNext();
          break;
        case "ArrowLeft":
          event.preventDefault();
          onPrevious();
          break;
        case "Escape":
          event.preventDefault();
          onClose?.();
          break;
        default:
          break;
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [onNext, onPrevious, onClose]);
}

